    <div className="messages" onClick = {toggleDropdown}>
      <i className="fa-solid fa-envelope"></i> 
      <span></span> 
      {dropdownVisible && (
    <div className = "dropdown">
<p>Message</p>
<p>Message</p>
<p>Message</p>
    </div>
  )}
    </div>
);
}

export default Messages;
