    setDropdownVisible((prev)=>!prev);
  };

  return (
    <div className="user" onClick = {toggleDropdown}>
      <i className="fa-solid fa-user"></i> {/* Ikona korisnika */}
      <span></span>
      {dropdownVisible && (
    <div className = "dropdownUser">
<p>Account settings</p>
